import React, { useState } from "react";
import "./FunctionCard.css";

interface Function {
  name: string;
  description: string;
  example: string;
}

interface FunctionCardProps {
  title: string;
  functions: Function[];
}

const FunctionCard: React.FC<FunctionCardProps> = ({ title, functions }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <div
      className={`function-card ${isExpanded ? "expanded" : ""}`}
      onClick={() => setIsExpanded(!isExpanded)}
    >
      <div className="function-card-header">
        <h3>{title}</h3>
        <span className="expand-icon">{isExpanded ? "−" : "+"}</span>
      </div>
      {isExpanded && (
        <div className="function-card-content">
          {functions.map((func, index) => (
            <div key={index} className="function-item">
              <h4>{func.name}</h4>
              <p>{func.description}</p>
              <code>{func.example}</code>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FunctionCard;
